import React,{ Component } from 'react';
//import UploadServices from '../services/UploadFiles';
import { Button, Form, Row, Col } from 'react-bootstrap';

class AddJournals extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formData: {
              headLine: '',
              description: '',
              newDate: '',
              
            },
            fields: ["head","description"]
        };
    };

    handleChange = (e)=>{
        if(e.currentTarget.name == "headLine") this.setState({headLine: e.currentTarget.value});
        if(e.currentTarget.name == "description") this.setState({description: e.currentTarget.value})
    }

     onSubmit = async (e) => {
      try {
        e.preventDefault()
        // this.setState({ headLine: this.state.headLine}),
        // this.setState({ description: this.state.description}),

        // const res = await UserServices.addUser(this.state.formData);
        // console.log('qwewqw', res)
        alert("Add user successfully");
      } catch (err) {
        console.log('somting went wrong!')
      }
    };

    
    
  
      render () {
          return (
            <div style={{ display: 'flex', height: '100vh', justifyContent: 'center', width: '400px', marginLeft: '20px' }}>
                <Form >
                     <Form.Group  as={Row} controlId="formBasicEmail">
                            <Form.Label>HeadLines</Form.Label>
                            <Form.Control name="text" type="email" placeholder="Enter email" onChange={(e)=>this.handleChange(e)} />
                     </Form.Group>

                     <Form.Group  as={Row} controlId="formBasicPassword">
                        <Form.Label>Description</Form.Label>
                        <textarea name="description" type="textarea" placeholder="Enter Description"  onChange={(e)=>this.handleChange(e)} />
                     </Form.Group>

                </Form>
            </div>
          );
      }
};
export default AddJournals
