import React, { Component } from "react";
import AddJournals from "./AddJournals";
import moment from "moment";
import "../Css/cardViewPage.css";

class CardViewPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      JournalData: [],
    };
  }

  componentDidMount() {
    let Journal = localStorage.getItem("journalData");
    console.log(Journal);
    // let dateWithDay =  moment(Journal.newDate); // Thursday Feb 2015
    // console.log(dateWithDay.day(), Journal.newDate);
    // Journal['newDate'] = dateWithDay;
    //this.setState({ JournalData: Journal });
    let obj = this.state.JournalData
    obj = Journal;
    console.log(obj);
  }

  render() {
    return (
      <div className="body">
          <div className="heading-tag">
          <h1 > All Journal</h1>
          </div>
        <div className="container">

              { this.state.JournalData.forEach((jurnal)=> (
                  <div>
                   <div className="date">
                  <h5 className="font-style">
                    {moment(jurnal.newDate).format("dddd")}
                  </h5>
                  <h5>
                    {moment(jurnal.newDate).format("DD-MMM-YYYY")}
                  </h5>
                </div>
                <div className="data-field">
                  <h1>{jurnal.headLine}</h1>
                  <p>{jurnal.description}</p>
                </div>
                </div>

              ))}

          <div className="image-div">
            <img src="http://picsum.photos/100/100" style={{ borderRadius: '5px'}} alt="image" />
          </div>
        </div>
      </div>
    );
  }
}

export default CardViewPage;
