import React, { Component } from "react";
import AddJournals from "./AddJournals";
import moment from "moment";
import "../Css/cardViewPage.css";

class CardViewPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      JournalData: [],
    };
  }

  componentDidMount() {
    let Journal = JSON.parse(localStorage.getItem("journalData"));
    console.log((Journal), typeof(Journal));
    // let dateWithDay =  moment(Journal.newDate); // Thursday Feb 2015
    // console.log(dateWithDay.day(), Journal.newDate);
    // Journal['newDate'] = dateWithDay;
    Journal.map(item => {
      this.setState( state => {
        this.state.JournalData.push(Journal)
      } );
    })
    let obj = this.state.JournalData
    obj = Journal;
    console.log(obj, Array.isArray(obj));
    console.log("data", this.state.JournalData);
  }

  render() {
    return (
      <div className="body">
          <div className="heading-tag">
          <h1 > All Journal</h1>
          </div>
        <div className="container">

              { this.state.JournalData.forEach((journal)=> (
                  <div>
                   <div className="date">
                  <h5 className="font-style">
                    {moment(journal.newDate).format("dddd")}
                  </h5>
                  <h5>
                    {moment(journal.newDate).format("DD-MMM-YYYY")}
                  </h5>
                </div>
                <div className="data-field">
                  <h1>{journal.headLine}</h1>
                  <p>{journal.description}</p>
                </div>
                </div>

              ))}

          <div className="image-div">
            <img src="http://picsum.photos/100/100" style={{ borderRadius: '5px'}} alt="image" />
          </div>
        </div>
      </div>
    );
  }
}

export default CardViewPage;
