import React, { Component } from "react";
import AddJournals from "./AddJournals";
import moment from "moment";
import "../Css/cardViewPage.css";

class CardViewPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      JournalData: {},
    };
  }

  componentDidMount() {
    let Journal = JSON.parse(localStorage.getItem("journalData"));
    // let dateWithDay =  moment(Journal.newDate); // Thursday Feb 2015
    // console.log(dateWithDay.day(), Journal.newDate);
    // Journal['newDate'] = dateWithDay;
    this.setState({ JournalData: { ...Journal } });
    let obj = this.state.JournalData;
    obj = { ...Journal };
    console.log(obj);
  }

  render() {
    return (
      <div className="body">
          <div className="heading-tag">
          <h1 > Journal</h1>
          </div>
        <div className="container">
          <div className="date">
            <h5 className="font-style">
              {moment(this.state.JournalData.newDate).format("dddd")}
            </h5>
            <h5>
              {moment(this.state.JournalData.newDate).format("DD-MMM-YYYY")}
            </h5>
          </div>
          <div className="data-field">
            <h1>{this.state.JournalData.headLine}</h1>
            <p>{this.state.JournalData.description}</p>
          </div>
          <div>
            <img src="http://picsum.photos/90/90" alt="image" />
          </div>
        </div>
      </div>
    );
  }
}

export default CardViewPage;
