import React,{ Component } from 'react';
//import UploadServices from '../services/UploadFiles';
import { Button, Form, Row, Col } from 'react-bootstrap';
import '../../src/Css/addJournals.css';

class AddJournals extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formData: {
              headLine: '',
              description: '',
              newDate: '',
              
            },
            fields: ["head","description"]
        };
    };

    handleChange = (e)=>{
        if(e.currentTarget.name == "headLine") this.setState({headLine: e.currentTarget.value});
        if(e.currentTarget.name == "description") this.setState({description: e.currentTarget.value})
    }

     onSubmit = async (e) => {
      try {
        e.preventDefault()
        // this.setState({ headLine: this.state.headLine}),
        // this.setState({ description: this.state.description}),

        // const res = await UserServices.addUser(this.state.formData);
        // console.log('qwewqw', res)
        alert("Add user successfully");
      } catch (err) {
        console.log('somting went wrong!')
      }
    };

    
    
  
      render () {
          return (
            <div class="container">
                <Form >
                     <Form.Group  style={{ display: 'flex',}}  controlId="formBasicEmail">
                            <Form.Label >HeadLines:</Form.Label>
                            <Form.Control style={{ marginLeft: '50px',}} name="headLine" type="text" placeholder="Enter email" onChange={(e)=>this.handleChange(e)} />
                     </Form.Group>

                     <Form.Group  style={{ display: 'flex',}} controlId="formBasicPassword">
                        <Form.Label >Description:</Form.Label>
                        <textarea style={{ marginLeft: '50px', width: '600px'}} rows="4" name="description" type="textarea" placeholder="Enter Description"  onChange={(e)=>this.handleChange(e)} />
                     </Form.Group>
                     <Form.Group  style={{ display: 'flex',}} controlId="formBasicPassword">
                        <Form.Label >Add Date</Form.Label>
                        <Form.Control style={{ marginLeft: '50px', width: '600px'}} rows="4" name="newDate" type="date" placeholder="Enter Description"  onChange={(e)=>this.handleChange(e)} />
                     </Form.Group>
                     <Form.Group as={Row}>
                <Col sm={{ span: 10, offset: 2 }}>
                  <Button onClick={this.onSubmit}>Add Journal</Button>
                </Col>
              </Form.Group>

                </Form>
            </div>
          );
      }
};
export default AddJournals
