import React,{ Component } from 'react';
//import UploadServices from '../services/UploadFiles';
import { Button, Form, Row, Col } from 'react-bootstrap';
import '../../src/Css/addJournals.css';

class AddJournals extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formData: {
              headLine: '',
              description: '',
              newDate: '',
              
            },
            fields: ["head","description"]
        };
    };

    handleChange = (e)=>{
      console.log(e);
        if(e.currentTarget.name == "headLine") this.setState({headLine: e.currentTarget.value});
        if(e.currentTarget.name == "description") this.setState({description: e.currentTarget.value});
        if(e.currentTarget.name == "newDate") this.setState({description: e.currentTarget.value})

    }

     onSubmit = async (e) => {
      try {
        e.preventDefault()
        let a = this.state.headLine;
        let b = this.state.description
        let c = this.state.newDate
        console.log(a,b,c);
        // this.setState({ headLine: this.state.headLine}),
        // this.setState({ description: this.state.description}),

        // const res = await UserServices.addUser(this.state.formData);
        // console.log('qwewqw', res)
        alert("Add user successfully");
      } catch (err) {
        console.log('somting went wrong!')
      }
    };

    
    
  
      render () {
          return (
            <div class="container">
                <form >

                     <div class="form-group"   controlId="formBasicEmail">
                            <label class="label">HeadLines:</label>
                            <input class="input" name="headLine" type="text" placeholder="Enter Head Line" onChange={(e)=>this.handleChange(e)} />
                     </div>

                     <div  class="form-group" controlId="formBasicPassword">
                        <label class="label" >Description:</label>
                        <textarea  class="input" rows="4" name="description" type="textarea" placeholder="Enter Description"  onChange={(e)=>this.handleChange(e)} />
                     </div>

                     <div  class="form-group" controlId="formBasicPassword">
                        <label class="label" >Add Date:</label>
                        <input class="input" style={{}} rows="4" name="newDate" type="date" placeholder="Enter Description"  onChange={(e)=>this.handleChange(e)} />
                     </div>

                     <div as={Row}class="form-group" >
                        <Col sm={{ span: 10, offset: 2 }}>
                        <Button onClick={this.onSubmit}>Add Journal</Button>
                        </Col>
                    </div>

                </form>
            </div>
          );
      }
};
export default AddJournals
